import 'bootstrap/dist/css/bootstrap.min.css';
import './BuyATicket.css';
import { useEffect, useState } from 'react';
import { Row, Col, Card, Button, Form, Container } from 'react-bootstrap';

import TicketSectionSelection from '../../components/TicketSectionSelection/TicketSectionSelection.js';
import TicketFullSeatSelection from '../../components/TicketFullSeatSelection/TicketFullSeatSelection.js';
import TicketSingleSeatSelection from '../../components/TicketSingleSeatSelection/TicketSingleSeatSelection.js';
import TicketFighterSelection from '../../components/TicketFighterSelection/TicketFighterSelection.js';

function BuyATicket() {
  const [availableTickets, setSeatAvailability] = useState(null);
  const [rowType, setRowType] = useState('');
  const [isAtEnd, setAtEnd] = useState(false);
  const [needInfo, setNeedInfo] = useState(true);

  const [user, setUser] = useState('');
  const [checkUser1, setCheckUser1] = useState('');
  const [checkUser2, setCheckUser2] = useState('');

  const [section, setSection] = useState('');
  const [seat, setSeatNumber] = useState('');
  const [price, setPrice] = useState(0);
  const [fighter, setFighter] = useState('');

  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSeatAvailability = async () => {
      const response = await fetch('/api/ticket');
      const json = await response.json();
      if (response.ok) {
        setSeatAvailability(json);
      }
    };
    fetchSeatAvailability();
  }, []);

  const handleSeatChange = (e) => {
    setError(null);
    setSeatNumber(e.target.value);

    const seatX = e.target.value;
    const row = seatX.charAt(0);

    switch (section.charAt(0)) {
      case 'A':
      case 'B':
      case 'C':
      case 'D':
      case 'E':
        if (row === 'A' || row === 'B') setPrice(92.5);
        else setPrice(67.5);
        break;
      case 'F':
        setPrice(92.5);
        break;
      case 'G':
        setPrice(115.0);
        break;
    }
  };

  const handleFighter = (e) => {
    setError(null);
  };

  function onSetEmail() {
    if (
      checkUser1 !== '' &&
      checkUser1 === checkUser2 &&
      checkUser2.includes('@') &&
      checkUser1.includes('.com')
    ) {
      setError(null);
      setUser(checkUser1);
    } else {
      setError('Emails do not match or are invalid.');
    }
  }

  function onBuyClick() {
    if (fighter !== '' && checkUser1 !== '' && checkUser1 === checkUser2) {
      setError(null);
      handleBuy();
    } else {
      setError('Please complete all selections.');
    }
  }

  const handleBuy = async () => {
    const purchaseSeat = {
      user,
      section,
      seat,
      price,
      fighter,
    };

    const response = await fetch('https://sirrocpromotions.onrender.com/api/ticket/', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(purchaseSeat),
    });

    const json = await response.json();

    if (!response.ok) {
      setError(json.error);
    }
    if (response.ok) {
      setError(null);
      const row = seat.charAt(0);

      switch (section.charAt(0)) {
        case 'S':
          window.location.href = 'https://buy.stripe.com/28E8wQ1GD973bks7pPgbm00';
          break;
        case 'A':
        case 'B':
        case 'C':
        case 'D':
        case 'E':
          if (row === 'A' || row === 'B') {
            window.location.href = 'https://buy.stripe.com/eVq00k4SPab72NW8tTgbm01';
          } else {
            window.location.href = 'https://buy.stripe.com/eVq28s0Cz9736089xXgbm02';
          }
          break;
        case 'F':
          window.location.href = 'https://buy.stripe.com/bJe00k0Czern3S0aC1gbm03';
          if (section.charAt(1) > 1) {
            window.location.href = 'https://buy.stripe.com/4gM7sMclhab7agodOdgbm04';
          }
          break;
        case 'G':
          window.location.href = 'https://buy.stripe.com/14AcN60Cz0AxfAI11rgbm05';
          break;
      }
    }
  };

  function handleClear() {
    window.location.reload();
  }

  return (
    <div className="container-fluid" style={{ backgroundColor: '#000', paddingBottom: '5rem', width: '100%' }}>
      <Row>
        {/* Sidebar - Ticket Summary */}
        <Col md={3} style={{ position: 'sticky', top: '15vh' }}>
          <Card className="shadow-lg border-warning" style={{ backgroundColor: '#1a1a1a', color: 'white' }}>
            <Card.Body>
              <h4 className="text-warning">Your Ticket</h4>
              <hr className="border-warning" />
              <p><strong>Section:</strong> {section || '-'}</p>
              <p><strong>Seat:</strong> {seat || '-'}</p>
              <p><strong>Fighter:</strong> {fighter || '-'}</p>
              <p className="h5 text-warning"><strong>Price:</strong> ${price.toFixed(2)}</p>
              <Button variant="outline-danger" size="sm" onClick={handleClear}>Clear</Button>
            </Card.Body>
          </Card>
        </Col>

        {/* Main Area */}
        <Col md={9}>
          <Container className="text-light py-4">
            <h1 className="mb-4">Buy A Ticket</h1>

            {/* Email Step */}
            <Card className="mb-4 bg-dark text-light border-warning">
              <Card.Body>
                <h5 className="text-warning">Step 1: Enter Your Email</h5>
                <Form>
                  <Form.Group className="mb-3">
                    <Form.Control
                      type="email"
                      placeholder="Enter email"
                      value={checkUser1}
                      onChange={(e) => setCheckUser1(e.target.value)}
                    />
                  </Form.Group>
                  <Form.Group className="mb-3">
                    <Form.Control
                      type="email"
                      placeholder="Re-type email"
                      value={checkUser2}
                      onChange={(e) => setCheckUser2(e.target.value)}
                    />
                  </Form.Group>
                  <Button variant="warning" onClick={onSetEmail}>Set Email</Button>
                  {user && <p className="mt-2">Email: {user}</p>}
                </Form>
              </Card.Body>
            </Card>

            {/* Section Selection */}
            <Card className="mb-4 bg-dark text-light border-warning">
              <Card.Body>
                <h5 className="text-warning">Step 2: Select Section</h5>
                <TicketSectionSelection onSectionChange={setSection} setSeat={setSeatNumber} rowType={setRowType} isAtEnd={setAtEnd} needInfo={setNeedInfo} setPrice={setPrice} />
              </Card.Body>
            </Card>

            {/* Seat Selection */}
            {rowType === 'full' && (
              <Card className="mb-4 bg-dark text-light border-warning">
                <Card.Body>
                  <h5 className="text-warning">Step 3: Select Seat</h5>
                  <TicketFullSeatSelection onSeatChange={handleSeatChange} isAtEnd={setAtEnd} />
                </Card.Body>
              </Card>
            )}
            {rowType === 'single' && (
              <Card className="mb-4 bg-dark text-light border-warning">
                <Card.Body>
                  <h5 className="text-warning">Step 3: Select Seat</h5>
                  <TicketSingleSeatSelection onSeatChange={handleSeatChange} isAtEnd={setAtEnd} />
                </Card.Body>
              </Card>
            )}

            {/* Fighter Selection */}
            {isAtEnd && (
              <Card className="mb-4 bg-dark text-light border-warning">
                <Card.Body>
                  <h5 className="text-warning">Step 4: Choose Fighter</h5>
                  <TicketFighterSelection onFighterChange={handleFighter} setFighter={setFighter} />
                </Card.Body>
              </Card>
            )}

            {/* Purchase */}
            <Card className="bg-dark text-light border-warning">
              <Card.Body className="text-center">
                <h5 className="text-warning mb-3">Ready to Buy?</h5>
                <Button variant="warning" size="lg" onClick={onBuyClick}>Buy Now</Button>
                {error && <p className="mt-2 text-danger">{error}</p>}
              </Card.Body>
            </Card>
          </Container>
        </Col>
      </Row>
    </div>
  );
}

export default BuyATicket;
