import 'bootstrap/dist/css/bootstrap.min.css';
import './BuyATicket.css';
import { useEffect, useState } from 'react';
import { Row, Col, Card, Button, Form, Container, ProgressBar } from 'react-bootstrap';
import TicketSectionSelection from '../../components/TicketSectionSelection/TicketSectionSelection.js';
import TicketFullSeatSelection from '../../components/TicketFullSeatSelection/TicketFullSeatSelection.js';
import TicketSingleSeatSelection from '../../components/TicketSingleSeatSelection/TicketSingleSeatSelection.js';
import TicketFighterSelection from '../../components/TicketFighterSelection/TicketFighterSelection.js';

function BuyATicket() {
  const [step, setStep] = useState(1);
  const [rowType, setRowType] = useState('');
  const [isAtEnd, setAtEnd] = useState(false);
  const [user, setUser] = useState('');
  const [checkUser1, setCheckUser1] = useState('');
  const [checkUser2, setCheckUser2] = useState('');
  const [section, setSection] = useState('');
  const [seat, setSeatNumber] = useState('');
  const [price, setPrice] = useState(0);
  const [fighter, setFighter] = useState('');
  const [error, setError] = useState(null);

  const progressValue = (step / 5) * 100;

  const onSetEmail = () => {
    if (
      checkUser1 !== '' &&
      checkUser1 === checkUser2 &&
      checkUser1.includes('@') &&
      checkUser1.includes('.')
    ) {
      setUser(checkUser1);
      setError(null);
      setStep(2);
    } else {
      setError('Emails must match and be valid.');
    }
  };

  const handleSeatChange = (e) => {
    setSeatNumber(e.target.value);
    setPrice(section.charAt(0) === 'G' ? 115 : 92.5);
    setStep(4);
  };

  const handleBuy = () => {
    if (fighter && user && section && seat) {
      alert('Proceeding to payment...');
    } else {
      setError('Please complete all selections.');
    }
  };

  return (
    <div style={{ backgroundColor: '#0d0d0d', minHeight: '100vh', color: '#fff' }}>
      {/* Hero Header */}
      <div
        style={{
          background: 'linear-gradient(rgba(0,0,0,0.6), rgba(0,0,0,0.9)), url("/ticket-bg.jpg") center/cover',
          padding: '4rem 2rem',
          textAlign: 'center',
        }}
      >
        <h1 style={{ fontSize: '3rem', fontWeight: 'bold', color: '#ffc107' }}>Buy Your Ticket</h1>
        <p className="lead" style={{ color: '#ccc' }}>
          Secure your spot for the ultimate fight night experience
        </p>
      </div>

      <Container fluid className="py-4">
        <Row>
          {/* Main Selection Area */}
          <Col lg={8} className="px-4">
            {/* Progress Bar */}
            <ProgressBar now={progressValue} variant="warning" className="mb-4" style={{ height: '10px', borderRadius: '5px' }} />

            {/* Step 1 - Email */}
            {step === 1 && (
              <Card className="glass-card mb-4">
                <Card.Body>
                  <h4 className="mb-3 text-warning">Step 1: Enter Your Email</h4>
                  <Form>
                    <Form.Control
                      type="email"
                      placeholder="Enter email"
                      className="mb-3"
                      value={checkUser1}
                      onChange={(e) => setCheckUser1(e.target.value)}
                    />
                    <Form.Control
                      type="email"
                      placeholder="Re-type email"
                      className="mb-3"
                      value={checkUser2}
                      onChange={(e) => setCheckUser2(e.target.value)}
                    />
                    <Button variant="warning" onClick={onSetEmail} className="w-100">Continue</Button>
                  </Form>
                  {error && <p className="text-danger mt-2">{error}</p>}
                </Card.Body>
              </Card>
            )}

            {/* Step 2 - Section Selection */}
            {step === 2 && (
              <Card className="glass-card mb-4">
                <Card.Body>
                  <h4 className="mb-3 text-warning">Step 2: Choose Your Section</h4>
                  <TicketSectionSelection
                    onSectionChange={(sec) => { setSection(sec); setStep(3); }}
                    setSeat={setSeatNumber}
                    rowType={setRowType}
                    isAtEnd={setAtEnd}
                    setPrice={setPrice}
                  />
                </Card.Body>
              </Card>
            )}

            {/* Step 3 - Seat Selection */}
            {step === 3 && rowType && (
              <Card className="glass-card mb-4">
                <Card.Body>
                  <h4 className="mb-3 text-warning">Step 3: Select Your Seat</h4>
                  {rowType === 'full' && <TicketFullSeatSelection onSeatChange={handleSeatChange} />}
                  {rowType === 'single' && <TicketSingleSeatSelection onSeatChange={handleSeatChange} />}
                </Card.Body>
              </Card>
            )}

            {/* Step 4 - Fighter Selection */}
            {step === 4 && (
              <Card className="glass-card mb-4">
                <Card.Body>
                  <h4 className="mb-3 text-warning">Step 4: Choose Fighter</h4>
                  <TicketFighterSelection
                    setFighter={(f) => { setFighter(f); setStep(5); }}
                  />
                </Card.Body>
              </Card>
            )}

            {/* Step 5 - Confirm & Buy */}
            {step === 5 && (
              <Card className="glass-card">
                <Card.Body className="text-center">
                  <h4 className="mb-3 text-warning">Step 5: Confirm Purchase</h4>
                  <Button variant="warning" size="lg" className="w-100" onClick={handleBuy}>
                    Buy Now - ${price.toFixed(2)}
                  </Button>
                </Card.Body>
              </Card>
            )}
          </Col>

          {/* Sidebar Summary */}
          <Col lg={4} className="px-4 mt-4 mt-lg-0">
            <Card className="glass-card">
              <Card.Body>
                <h5 className="text-warning">Order Summary</h5>
                <hr className="border-warning" />
                <p><strong>Email:</strong> {user || '-'}</p>
                <p><strong>Section:</strong> {section || '-'}</p>
                <p><strong>Seat:</strong> {seat || '-'}</p>
                <p><strong>Fighter:</strong> {fighter || '-'}</p>
                <p className="h5 text-warning"><strong>Total:</strong> ${price.toFixed(2)}</p>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default BuyATicket;
